@dp.message_handler(commands=['start'])
async def start(msg: types.Message):
    user_id = msg.from_user.id
    name = msg.from_user.full_name
    log[user_id] = {'name': name, 'start': datetime.now().strftime('%Y-%m-%d %H:%M:%S')}
    save_data()
