async def show_ayah(msg: types.Message):
    user_id = msg.from_user.id
    user_state[user_id] = {"mode": "surah", "index": 0, "name": msg.text}
    await send_surah(user_id, msg.chat.id)

async def send_surah(user_id, chat_id):
    state = user_state[user_id]
    if state["mode"] != "surah":
        return
    sura = surahs[state["name"]]
    index = max(0, min(state["index"], len(sura) - 1))
    user_state[user_id]["index"] = index
    ayah = sura[index]
    text = f"<b>{ayah['arabic']}</b>\n\n{ayah['translation']}"
    markup = ReplyKeyboardMarkup(resize_keyboard=True)
    markup.add(KeyboardButton('⬅️ Назад'), KeyboardButton('➡️ След'), KeyboardButton("⬅️ В меню"))
    m = await bot.send_message(chat_id, text, reply_markup=markup)
    # noinspection PyUnresolvedReferences
    await delete_in(60, m)

@dp.message_handler(lambda m: m.from_user.id in user_state and user_state[m.from_user.id]["mode"] == "surah" and m.text in ['➡️ След', '⬅️ Назад'])
async def ayah_nav(msg: types.Message):
    user_id = msg.from_user.id
    if msg.text == '➡️ След':
        user_state[user_id]["index"] += 1
    else:
        user_state[user_id]["index"] -= 1
