async def reminder():
    while True:
        await asyncio.sleep(650)
        for uid in log:
            r = random.choice(['surah', 'hadith'])

            if r == 'surah' and surahs:
                sura_name = random.choice(list(surahs.keys()))
                ayahs = surahs.get(sura_name, [])
                if not ayahs: continue
                ayah = random.choice(ayahs)
                text = f"<b>📖 Аят из Священного Корана — Слово Аллаха ✨</b>\n\n<b>{ayah['arabic']}</b>\n\n{ayah['translation']}"

            elif r == 'hadith' and hadiths:
                h = random.choice(hadiths)
                text = f"<b>Хадис Пророка ﷺ — Свет наставления 📜</b>\n\n<b>{h['arabic']}</b>\n\n{h['translation']}\n\nИсточник: {h['source']}"
            else:
                continue  # если данных нет — пропускаем

            try:
                await bot.send_message(uid, text)
            except:
                pass

            async def delete_in(sec, msg):
                await asyncio.sleep(sec)
                try:
                    await msg.delete()
                except:
                    pass

if __name__ == '__main__':
    loop = asyncio.get_event_loop()
    loop.create_task(reminder())
    executor.start_polling(dp, skip_updates=True)